---------------------------------------------------------
-- AABB helper (rectangle vs rectangle)
---------------------------------------------------------
function AABBRect(ax, ay, aw, ah, bx, by, bw, bh)
    return ax < bx + bw and bx < ax + aw and ay < by + bh and by < ay + ah
end

---------------------------------------------------------
-- LEVEL PARSER: creates objects with sprite tile IDs
---------------------------------------------------------

function LoadLevel(levelID)

    if not Levels[levelID] then
        CurrentLevel = nil
        CurrentLevelID = 0
        GameState.active = GameState.menu
        return
    end

    CurrentLevelID = levelID
    CurrentLevel   = Levels[levelID]
    -- Apply background theme (if specified by level)
    if Bg and Bg.SetTheme then Bg.SetTheme(CurrentLevel.theme or "default") end

    -- clear object lists
    GroundObjects  = {}
    SpikeObjects   = {}
    CoinObjects    = {}
    BlockObjects   = {}
    FinishObjects  = {}

    TransparentObjects = {}
    PlatformObjects    = {}
    MiniSpikeObjects   = {}
    BigSpikeObjects    = {}
    FlippedMiniSpikeObjects = {}

    local rows = CurrentLevel.rows

    for row = 1, #rows do
        local line = rows[row]

        for col = 1, #line do
            local ch = line:sub(col, col)
            local x  = (col - 1) * TILE
            local y  = (row - 1) * TILE

            if ch == "G" then
                table.insert(GroundObjects, {x = x, y = y, width = TILE, height = TILE})

            elseif ch == "B" then
                table.insert(BlockObjects, {x = x, y = y, width = TILE, height = TILE,})

            elseif ch == "S" then
                table.insert(SpikeObjects, {x = x, y = y, width = TILE, height = TILE})

            elseif ch == "C" then
                table.insert(CoinObjects, {x = x, y = y, width = TILE, height = TILE})

            elseif ch == "F" then
                table.insert(FinishObjects, {x = x, y = y, width = TILE, height = TILE})
            
            elseif ch == "P" then
                table.insert(PlatformObjects, {x = x, y = y + (TILE/2), width = TILE, height = TILE/2})

            elseif ch == "V" then
                table.insert(MiniSpikeObjects, {x = x, y = y + (TILE/2), width = TILE, height = TILE/2})

            elseif ch == "W" then
                table.insert(BigSpikeObjects, {x = x, y = y - TILE, width = TILE, height = TILE * 2})

            elseif ch == "v" then
                table.insert(FlippedMiniSpikeObjects, {x = x, y = y, width = TILE, height = TILE/2})
            end
        end
    end

    -- reset player
    Player.x = 100
    Player.y = WindowHeight - 150
    Player.yVelocity = 0
    Player.isOnGround = false
    TotalCoinsCollected = 0
end

return level
